package fit.tdc.edu.vn.cafemanagement.fragment.loginimport android.app.Applicationimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport fit.tdc.edu.vn.cafemanagement.data.data_source.firebase.AuthDataSourceImplimport fit.tdc.edu.vn.cafemanagement.data.repository.auth.AuthRepositoryImpl/** * ViewModel provider factory to instantiate LoginViewModel. * Required given LoginViewModel has a non-empty constructor */class LoginViewModelFactory : ViewModelProvider.Factory {    @Suppress("UNCHECKED_CAST")    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(LoginViewModel::class.java)) {            return LoginViewModel(                loginRepository = AuthRepositoryImpl(                    dataSource = AuthDataSourceImpl()                ),                application = Application()            ) as T        }        throw IllegalArgumentException("Unknown ViewModel class")    }}